+        navigation: {
+            nextEl: '.swiper-button-next',
+            prevEl: '.swiper-button-prev',
+        },
+        autoplay: {
+            delay: 3000,
+            disableOnInteraction: false,
+        },
+        breakpoints: {
+            320: {
+                slidesPerView: 1, // 모바일에서는 1개의 슬라이드
+                spaceBetween: 10
+            },
+            768: {
+                slidesPerView: 2, // 태블릿에서는 2개의 슬라이드
+                spaceBetween: 20
+            },
+            1024: {
+                slidesPerView: 4, // 1024px 이상에서는 4개의 슬라이드
+                spaceBetween: 30
+            }
+        }
+    });
+
+
+    document.querySelectorAll('.faq-toggle').forEach(button => {
+        button.addEventListener('click', () => {
+            const expanded = button.getAttribute('aria-expanded') === 'true';
+            button.setAttribute('aria-expanded', !expanded);
+            const answer = button.nextElementSibling;
+
+            if (!expanded) {
+                answer.style.maxHeight = answer.scrollHeight + "px";
+                button.querySelector('.toggle-icon').textContent = '-';
+            } else {
+                answer.style.maxHeight = null;
+                button.querySelector('.toggle-icon').textContent = '+';
+            }
+        });
+    });
+
+const feed = new Instafeed({
+    accessToken: 'IGQWRPZAHVyT3B5ZAUVGZAkllR0lKeGhHSTBWVWtuU0s0ZAWVZAckRyZA0s3UjUzVF9IVnI2dGZAyRkpER1JFTkdRaDgwcXoyLW5KQTNnTDJWaVBTRngzcXJJYXhWZAFRIX1JGQWlOb2FDVHJIUzlUZAlFtN2FPVDJiTFhXTm8ZD',
