+    target: 'instafeed',
+    template: '<a href="@{{link}}" target="_blank"><img src="@{{image}}" alt="@{{caption}}" title="@{{caption}}" /></a>'
+});
+feed.run();
+fetch('https://graph.instagram.com/me?fields=id,username,account_type,media_count,followers_count,follows_count,profile_picture_url&access_token=IGQWRPZAHVyT3B5ZAUVGZAkllR0lKeGhHSTBWVWtuU0s0ZAWVZAckRyZA0s3UjUzVF9IVnI2dGZAyRkpER1JFTkdRaDgwcXoyLW5KQTNnTDJWaVBTRngzcXJJYXhWZAFRIX1JGQWlOb2FDVHJIUzlUZAlFtN2FPVDJiTFhXTm8ZD')
