+    margin: 0;
+    padding: 0;
+    border: none;
+}
+
+#instafeed {
+    display: grid;
+    grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
+    grid-gap: 0;
+}
+
+#instafeed a {
+    display: block;
+    overflow: hidden;
+    position: relative;
+}
+
+#instafeed img {
+    width: 310px;
+    height: 310px;
+    object-fit: cover;
+}
+
+#instafeed a:hover img {
+    transform: scale(1.05);
+}
+
+@media (max-width: 768px) {
+    #instafeed {
+        grid-template-columns: repeat(2, 1fr); /* 중간 크기 화면에서는 2열 */
+    }
+}
+
+@media (max-width: 480px) {
+    #instafeed {
+        grid-template-columns: 1fr; /* 작은 화면에서는 1열 */
+    }
+}
+.instagram-profile {
+    display: flex;
+    justify-content: center; /* 가운데 정렬 */
+    align-items: center;
+    padding: 20px;
+    background-color: #fff; /* 배경색 */
+    border-radius: 8px; /* 모서리 둥글게 */
+    box-shadow: 0 4px 8px rgba(0,0,0,0.1); /* 그림자 효과 */
+    max-width: 600px; /* 최대 너비 설정 */
+    margin: 20px auto; /* 자동 마진으로 중앙 배치 */
+}
+
+.profile-pic {
+    width: 80px; /* 프로필 이미지 크기 */
+    height: 80px;
+    border-radius: 50%; /* 원형 이미지 */
+    margin-right: 20px; /* 이미지와 정보 사이 마진 */
+}
+
+.profile-info {
+    text-align: left; /* 텍스트 왼쪽 정렬 */
+}
+
+.username {
+    font-size: 20px; /* 사용자 이름 폰트 크기 */
+    font-weight: bold;
+    margin-bottom: 10px; /* 사용자 이름과 통계 사이의 여백 */
+}
+
+.stats {
+    font-size: 16px; /* 통계 폰트 크기 */
+    color: #666; /* 텍스트 색상 */
+}
+
+.follow-btn {
+    padding: 10px 20px;
+    background-color: #0095f6; /* Instagram 파란색 */
+    color: white;
+    border: none;
+    border-radius: 4px;
+    cursor: pointer; /* 버튼 위에 마우스 오버시 포인터 모양 */
+    margin-top: 10px; /* 버튼과 통계 사이 여백 */
+}
+
+/* FAQ Section Styling */
+.faq-section {
+    max-width: 1000px;
+    margin: 0 auto;
+    padding: 20px;
+}
+
+.faq-title {
+    font-size: 24px;
+    text-align: center;
+    margin-bottom: 20px;
+}
+
+.faq-list {
+    list-style: none;
+    padding: 0;
+}
+
+.faq-item {
+    border-top: 1px solid #ccc;
+    padding: 10px 0;
+}
+
+.faq-toggle {
+    background: none;
+    border: none;
+    width: 100%;
+    text-align: left;
+    padding: 10px;
+    font-size: 18px;
+    cursor: pointer;
+}
+
+.toggle-icon {
+    float: right;
+}
+
+.faq-answer {
+    max-height: 0;
+    overflow: hidden;
+    transition: max-height 0.3s ease-out;
+    padding: 0 10px;
+}
+
+.faq-answer p {
+    margin-top: 10px;
+}
+
+</style>
+@endsection
